local ref = gui.Reference("MISC", "GENERAL", "Extra");
local fsgroup = gui.Groupbox( ref, "Fake Score" )
local showmenu = gui.Checkbox( fsgroup, "fakescore_enable", "Show Fake Score Menu",0 )

local Kills = 0
local Deaths = 0
local Assists = 0
local Score = 0
local Enable = false;

local function fakescorecallback()
    local me = entities.GetLocalPlayer();

    if Enable == true and me ~= nil then
        entities.GetPlayerResources():SetPropInt(Kills,"m_iKills",client.GetLocalPlayerIndex());
        entities.GetPlayerResources():SetPropInt(Deaths,"m_iDeaths",client.GetLocalPlayerIndex());
        entities.GetPlayerResources():SetPropInt(Assists,"m_iAssists",client.GetLocalPlayerIndex());
        entities.GetPlayerResources():SetPropInt(Score,"m_iScore",client.GetLocalPlayerIndex());
    end
end

callbacks.Register("Draw", "fakeScore", fakescorecallback);
function Menu()
if showmenu:GetValue() and gui.Reference("MENU"):IsActive() then
    mgui.max_component(10,100)
    mgui.menu(300,340,230,250,"Fake Score Menu",1)
    mgui.panel(10,15,210,230,"Fake Score",2,1)
    Kills = mgui.slider(20,30,200,1000,"Kills","",0,1,1)
    Deaths = mgui.slider(20,70,200,1000,"Deaths","",0,2,1)
    Assists = mgui.slider(20,110,200,1000,"Assists","",0,3,1)
    Score = mgui.slider(20,160,200,5000,"Score","",0,4,1)
    local Enablec = mgui.checkbox(25,200,200,"Enable",Enable,6,1) 
    if (Enablec == true) then Enable = true; elseif (Enablec == false) then Enable = false; end
    mgui.menu_mouse(1) 

end

end

callbacks.Register( "Draw", "fakescore_menu", Menu );
